import React from 'react'
import './Breadcrumb.css'
import arrow_icon  from '../assets/velikonocni-cupcaky.jpg'

const Breadcrumb = (props) => {
    const {product}=props;
    return (
    <div className='breadcrumb'>
        HOME <img src={arrow_icon} alt="" width={50}/>
        Shop <img src={arrow_icon} alt="" width={50}/>
        {product.category}
        <img src={arrow_icon} alt="" width={50}/>
        {product.name}
    </div>
    )
}

export default Breadcrumb
