import React, { createContext, useEffect, useState } from "react";
import all_recipes from "../components/assets/all-recipes";

export const ShopContext = createContext(null);

const ShopContextProvider = (props)=>{
    const [all_recipes, setAll_Recipes] = useState([]);
    useEffect(()=>{
        fetch('http://localhost:4000/allrecipes').then((response)=>response.json()).then((data)=>setAll_Recipes(data))
    },[])
    const getDefaultCart = ()=>{
        let cart ={};
        for (let index = 0; index < all_recipes.length+1; index++) {
            cart[index]=0;
        }
        return cart;
    }

    const [cartItems, setCartItems]= useState(getDefaultCart()); 
    
    const saveForLater= (recipeId)=>{
        setCartItems((prev)=>({...prev,[recipeId]:prev[recipeId]+1}));
        if(localStorage.getItem('auth-token')){
            fetch('http://localhost:4000/savelater',{
                method:'POST',
                headers:{
                    Accept:'application/json',
                    'auth-token':`${localStorage.getItem('auth-token')}`,
                    'Content-Type':'application/json'
                },
                body:JSON.stringify({"recipeId":recipeId}),
            }).then((response)=>response.json()).then((data)=>console.log(data)).catch((error)=>console.error('error:', error))
        }
    }
    const removeFromSave= (recipeId)=>{
        setCartItems((prev)=>({...prev,[recipeId]:prev[recipeId]-1}));
    }
    const getTotalCartAmount = ()=>{
        let totalAmount = 0;
        for(const item in cartItems){
            if(cartItems[item]>0){
                let itemInfo= all_recipes.find((recipe)=>recipe.id==Number(item))
                totalAmount+=itemInfo.new_price * cartItems[item];
            }
        }
    }
    const getTotalCartItems = ()=>{
        let totalItem = 0;
        for(const item in cartItems){
            if(cartItems[item] > 0){
                totalItem += cartItems[item];
            }
        }
        return totalItem;
    }

    const contextValue = {getTotalCartItems,getTotalCartAmount,all_recipes, cartItems,saveForLater,removeFromSave};

    return(
        <div>
        <ShopContext.Provider value={contextValue}>
            {props.children}
        </ShopContext.Provider>
        </div>
    )
}
export default ShopContextProvider
