import React, { useState, useEffect } from 'react';
import "./EditRecipe.css";

const EditRecipe = ({ recipeData }) => {
  const [image, setImage] = useState(false);
  const [recipeDetails, setRecipeDetails] = useState({
    name: "",
    image: "",
    category: "",
    servings: "",
    preparation_time:"",
    preparation_process: [], 
    source: ""
  });

  useEffect(() => {
    if (recipeData) {
      // If recipeData is provided (editing mode), populate recipeDetails with the provided data
      setRecipeDetails(recipeData);
    }
  }, [recipeData]);

  const imageHandler = (e) => {
    setImage(e.target.files[0]);
  };

  const changeHandler = (e) => {
    setRecipeDetails({ ...recipeDetails, [e.target.name]: e.target.value });
  };

  const Add_Recipe = async () => {
    // Add logic for adding or updating recipe data
  };

  // Rest of the component remains the same
};

export default EditRecipe;
