import React, { useState } from 'react'
import "./AddRecipe.css"
import upload_area from "./velikonocni-cupcaky.jpg"

const AddRecipe = () => {
  const [image, setImage]=useState(false);
  const [recipeDetails, setRecipeDetails]= useState({
    name:"",
    image:"",
    category:"",
    servings:"",
    ingredients:[],
    preparation_process:[],
    source:""
  })
  const imageHandler = (e)=>{
      setImage(e.target.files[0]);
  } 
  const changeHandler = (e)=>{
    setRecipeDetails({...recipeDetails,[e.target.name]:e.target.value})
  }
  const Add_Recipe = async ()=>{
    console.log(recipeDetails);
    let responseData;
    let recipe= recipeDetails;

    let formData = new FormData();
    formData.append('recipe',image);

    await fetch('http://localhost:4000/upload', {
      method:'POST',
      headers:{
        Accept:'application/json',
      },
      body:formData
    }).then((resp)=> resp.json()).then((data)=>{responseData=data});

    if(responseData.success){
      recipe.image = responseData.image_url;
      console.log(recipe);
      await fetch('http://localhost:4000/addrecipe',{
        method:'POST',
        headers:{
          Accept:'application/json',
          'Content-Type':'application/json',
        },
        body: JSON.stringify(recipe),
      }).then((resp)=>resp.json()).then((data)=>{
        data.success?alert("Recipe added"):alert("Failed")
        /*redo it so that it doesn just send an alert*/
      })
    }
  }
    // State to store input values
    const [inputs, setInputs] = useState(['']);
  
    // Function to handle input change
    const handleInputChange = (index, event) => {
      const newInputs = [...inputs];
      newInputs[index] = event.target.value;
      setInputs(newInputs);
    };
  
    // Function to add a new input field
    const handleAddInput = () => {
      setInputs([...inputs, '']);
    };
  
    // Function to remove an input field
    const handleRemoveInput = (index) => {
      const newInputs = [...inputs];
      newInputs.splice(index, 1);
      setInputs(newInputs);
    };
  return (
    <div className='add-recipe'>
      <div className="addrecipe-itemfield">
        <p>Název receptu</p>
        <input value={recipeDetails.name} onChange={changeHandler} type="text" name="name" placeholder='Název receptu' />
      </div>
      <div className="addrecipe-serving">
        <div className="addrecipe-serving-itemfield">
          <p>Počet porcí: </p>
          <input  type="text" name="servings" placeholder='Počet porcí' />
        </div>
      </div>
      <div className="addrecipe-time">
        <div className="addrecipe-time-itemfield">
          <p>Čas přípravy: </p>
          <input type="text" name="time" placeholder='Čas přípravy' />
        </div>
      </div>
      <div className="addrecipe-itemfield">
        <p>Kategorie: </p>
        <select value={recipeDetails.category} onChange={changeHandler} name="category" className='addrecipe-selector'>
          <option value="Sladké">Sladké</option>
          <option value="Předkrmy">Předkrmy</option>
          <option value="Nápoje">Nápoje</option>
          <option value="Polévky">Polévky</option>
          <option value="Hlavní chody">Hlavní chody</option>
          <option value="Saláty">Saláty</option>
        </select>
      </div>
      <div className="addrecipe-itemfield">
        <label htmlFor="file-input">
          <img src={image?URL.createObjectURL(image):upload_area} width="50px" className="addrecipe-thumbnail" alt="" />
        </label>
        <input onChange={imageHandler} type="file" name="image"  id="file-input" hidden/>
      </div>
      
    
    {/* Render input fields */}
    {inputs.map((value, index) => (
      <div key={index}>
        <input
          type="text"
          name='ingredients'
          onChange={(event) => handleInputChange(index, event)}
        />
        {/* Add button to remove input field */}
        <button onClick={() => handleRemoveInput(index)}>Odstraň pole</button>
      </div>
    ))}
    {/* Add button to add new input field */}
    <button onClick={handleAddInput}>Přidej další pole ingrediencí</button>
    {/* Display input values (for demonstration) */}
    <div>
      {inputs.map((value, index) => (
        <p key={index}>{value}</p>
      ))}
    </div>
    {/* Render input fields */}
    {inputs.map((value, index) => (
      <div key={index}>
        <input
          type="text"
          value={value}
          onChange={(event) => handleInputChange(index, event)}
        />
        {/* Add button to remove input field */}
        <button onClick={() => handleRemoveInput(index)}>Odstraň pole</button>
      </div>
    ))}
    {/* Add button to add new input field */}
    <button onClick={handleAddInput}>Přidej další pole v postupu</button>
    {/* Display input values (for demonstration) */}
    <div>
      {inputs.map((value, index) => (
        <p key={index}>{value}</p>
      ))}
    </div>
    
    <button onClick={()=>{Add_Recipe()}} className='addrecipe-btn'>Přidej recept</button>
  </div>
  )
}

export default AddRecipe
