import React, { createContext, useEffect, useState } from "react";
import all_recipes from "../components/assets/all-recipes";

export const ShopContext = createContext(null);

const ShopContextProvider = (props)=>{
    const [all_recipes, setAll_Recipes] = useState([]);
    /*useEffect(()=>{
        fetch('http:localhost:4000/allrecipes').then((response)=>response.json()).then((data)=>setAll_Recipes(data))
    },[])*/

    const contextValue = {all_recipes};
    return(
        <div>
            {console.log(all_recipes)}
        <ShopContext.Provider value={contextValue}>
            {props.children}
        </ShopContext.Provider>
        </div>
    )
}
export default ShopContextProvider
