import React from 'react'
import "./ReviewForm.css"

const ReviewForm = () => {
    const [formData, setFormData] = useState({ userName: '', rating: 0, comment: '' });

    const handleChange = e => {
      setFormData({ ...formData, [e.target.name]: e.target.value });
    };
  
    const handleSubmit = async e => {
      e.preventDefault();
      try {
        await axios.post('/reviews', formData);
        alert('Review submitted successfully!');
        setFormData({ userName: '', rating: 0, comment: '' });
      } catch (err) {
        console.error('Error submitting review:', err);
        alert('Failed to submit review. Please try again later.');
      }
    };
  
    return (
      <form onSubmit={handleSubmit}>
        <input type="text" name="userName" value={formData.userName} onChange={handleChange} placeholder="Your Name" required />
        <input type="number" name="rating" value={formData.rating} onChange={handleChange} min={1} max={5} placeholder="Rating (1-5)" required />
        <textarea name="comment" value={formData.comment} onChange={handleChange} placeholder="Your Review" required />
        <button type="submit">Submit Review</button>
      </form>
    );
}

export default ReviewForm
