import React from 'react'
import './CSS/ShopCategory.css'
import { useContext } from 'react'
import { ShopContext } from '../context/ShopContext'
import dropdown_icon from '../components/assets/velikonocni-cupcaky.jpg'
import Item from '../components/Items/Item'

const ShopCategory = (props) => {
  const {all_recipes} = useContext(ShopContext)
  return (
    <div className='shop-category'>
      <div className="shopcategory-indexSort">
      </div>
      <div className="shopcategory-products">
        {all_recipes.map((item,i)=>{
          if(props.category===item.category){
            return<Item key={i} id={item.id} name={item.name} image={item.image}/>
          }
          else {return null;}
        })}
      </div>
      
      
    </div>
    /*<div>
    <img className='shopcategory-banner' src={props.banner} alt="" />
    <div className="shopcategory-sort">
          sort by <img src={dropdown_icon} width="50px" alt="" />
        </div>
        <div className="shopcategory-loadmore">
        explore more
      </div>
<p>
          <span>Showing 1-12</span> out of 36
        </p>
      </div>*/
  )
}

export default ShopCategory
