import React from 'react'
import './DescriptionBox.css'
import all_recipes from '../assets/all-recipes'
import Recipe from '../Recipes/Recipe'

const DescriptionBox = (props) => {
  const {recipe}=props; 
    if(!recipe){
      return(
        <p>Missing data</p>
      )
    }
  return (
    <div className='descriptionbox'>
      <div className="descriptionbox-navigator">
        <div className="descriptionbox-nav-box">Suroviny</div>
        <div className="descriptionbox-nav-box fade">Hodnocen√≠</div>
        </div>
        <div className="descriptionbox-description">
          <p>
          {all_recipes.map((item, i)=>{
            return <Recipe key={i} id={item.id} ingredients={item.ingredients} 
            />
        })}
          </p>
          {recipe.preparation_process}
        </div>
    </div>
  )
}

export default DescriptionBox
